import React, { useState } from 'react';
import api from '../src/services/api.js';
import './App.css';

import logo from './assets/logo.svg';

function App() {
  const [email, setEmail] = useState('');

  async function handleSubmit(event) {
    event.preventDefault();
    const response = await api.post('/sessions', { email });

    console.log(response);
  }

  return (
    <div className="container">
      <img src={logo} alt="AirCnC"/>

      <div className="content">
        <p>Ofere√ßa <strong>spots</strong> para programadores e encontre <strong>talentos</strong> para a sua empresa</p>

        <form onSubmit={handleSubmit}>
          <label htmlFor="email">E-MAIL *</label>
          <input type="email" id="email" placeholder="Seu melhor e-mail" value={email} onChange={ event => setEmail(event.target.value) }/>
          <button type="submit" className="btnEntrar">Entrar</button>
        </form>
      </div>
    </div>
  );
}

export default App;
